INSERT INTO `topic` VALUES ('472', '2', '详见《非公办法》第三十九条。', '根据《非公办法》的规定，以下关于监事会描述正确的选项是（   ）。', 'B', '监事会应当在股东大会审议认定核心员工后，对核心员工名单发表明确意见', '监事会应当在股东大会审议认定核心员工前，对核心员工名单发表明确意见', '监事会可以在股东大会审议认定核心员工后对核心员工名单发表明确意见', '监事会无需对提交股东大会审议的核心员工名单发表明确意见', '', '', '85', '0', '2018-01-24 23:38:17');
